// Imports
// --------------------------------
import React, { useState } from 'react';
import { Header, Main, Item } from './styles';

// Render
// --------------------------------
export default () => {
    const [items, setItems] = useState([
        // {
        //     "name": "TXG Limpiador de Brochas de Maquillaje,10 Segundos de Secado Rápido Para Todos los Pinceles de Maquillaje",
        //     "url": "/TXG-Limpiador-Maquillaje-Segundos-Pinceles/dp/B0855T1V18/ref=sr_1_3?dchild=1&keywords=maquillaje&qid=1608068685&sr=8-3",
        //     "image": "https://m.media-amazon.com/images/I/61jyeWWv4rL._AC_UL320_.jpg",
        //     "price": "299.00"
        // }
    ]);

    return (
        <div>
            <Header background="https://pbs.twimg.com/profile_banners/1422058765/1482802543/1500x500">
                <div>
                    <a href="https://twitter.com/sagmmd">
                        <img alt="sagmmd"
                             src="https://pbs.twimg.com/profile_images/1336387092872617986/dB-XLxmd_400x400.jpg"/>
                        <h1>@sagmmd</h1>
                        <p>Personal Wishlist</p>
                    </a>
                </div>
                <button>Add Item</button>
            </Header>
            <Main>
                {items && items.length > 0
                    ? <section>
                        {items.map((i, k) => <Item key={`Item-${k}`}>
                            <dl>
                                <dt>...</dt>
                                <dd>Delete</dd>
                            </dl>
                            <span>${i.price}</span>
                            <img alt={i.name} src={i.image} />
                            <h3>{i.name}</h3>
                            <a href={`https://www.amazon.com.mx${i.url}`} target="_blank" rel="noopener noreferrer">View On Amazon</a>
                        </Item>)}
                    </section>
                    : <section><p>No items yet<br/><span>Start By Adding An Item</span></p></section>}
            </Main>
        </div>
    );
};
